const response = $json;

try {
  const result = response.chart.result[0];
  const meta = result.meta;

  const currentPrice = meta.regularMarketPrice;
  const previousClose = meta.previousClose;
  const change = currentPrice - previousClose;
  const changePercent = ((change / previousClose) * 100).toFixed(2);
  const volume = meta.regularMarketVolume;

  return {
    json: {
      symbol: meta.symbol,
      price: currentPrice.toFixed(2),
      change: change.toFixed(2),
      changePercent: changePercent + '%',
      volume: volume.toLocaleString('es-ES'),
      currency: meta.currency,
      marketState: meta.marketState,
      timestamp: new Date(meta.regularMarketTime * 1000).toLocaleString('es-ES'),
      dayHigh: meta.regularMarketDayHigh?.toFixed(2) || 'N/A',
      dayLow: meta.regularMarketDayLow?.toFixed(2) || 'N/A'
    }
  };
} catch (error) {
  return {
    json: {
      error: 'Error: ' + error.message
    }
  };
}

const data = $json;

const message = `üìä ${data.symbol} - ${data.marketState}

üí∞ Preu: ${data.price} ${data.currency}
üìà Canvi: ${data.change} (${data.changePercent})
üìä M√†xim: ${data.dayHigh}
üìâ M√≠nim: ${data.dayLow}
üì¶ Volum: ${data.volume}
üïê ${data.timestamp}`;

return {
  json: {
    ...data,
    formattedMessage: message
  }
};
